mapper.insert:
INSERT INTO pms_product_attribute (
product_attribute_category_id, name, select_type, input_type,
input_list, sort, filter_type, search_type,
related_status, hand_add_status, type
) VALUES (
#{productAttributeCategoryId}, #{name}, #{selectType}, #{inputType},
#{inputList}, #{sort}, #{filterType}, #{searchType},
#{relatedStatus}, #{handAddStatus}, #{type}
)

mapper.update:
UPDATE pms_product_attribute
product_attribute_category_id = #{productAttributeCategoryId}, name = #{name}, select_type = #{selectType}, input_type = #{inputType}, input_list = #{inputList}, sort = #{sort}, filter_type = #{filterType}, search_type = #{searchType}, related_status = #{relatedStatus}, hand_add_status = #{handAddStatus}, type = #{type}
WHERE id = #{id}

mapper.select:
SELECT * FROM pms_product_attribute
WHERE product_attribute_category_id = #{attributeCategoryId}
ORDER BY sort ASC

mapper.update:
UPDATE pms_product_attribute_category
SET attribute_count = attribute_count + #{delta}
WHERE id = #{attributeCategoryId}

UPDATE pms_product_attribute_category
SET param_count = param_count + #{delta}
WHERE id = #{attributeCategoryId}

UPDATE pms_product_attribute_value
SET value = #{value}
WHERE id = #{productAttributeValueId}

mapper.delete:
DELETE FROM pms_product_attribute_value
WHERE product_id = #{productId}

    AND product_attribute_id = #{attributeId}

mapper.select:
SELECT * FROM pms_product_attribute_value
WHERE product_id = #{productId}

    AND product_attribute_id = #{attributeId}

ORDER BY id ASC

mapper.delete:
DELETE FROM pms_product_category_attribute_relation
WHERE product_category_id = #{productCategoryId}
AND product_attribute_id = #{attributeId}
