mapper.select:
SELECT * FROM sms_coupon_history
WHERE member_id = #{memberId}

    AND use_status = #{useStatus}

SELECT c.* FROM sms_coupon c
WHERE c.id IN (
SELECT coupon_id FROM sms_coupon_product_relation WHERE product_id = #{productId}
UNION
SELECT coupon_id FROM sms_coupon_product_category_relation WHERE product_category_id = #{categoryId}
UNION
SELECT id FROM sms_coupon WHERE use_type = 0
)
